l>                            <textarea id="message" name="message" class="form-control" rows="4" placeholder="Write your message here..." required></textarea>                        </div>                                                <!-- Schedule At -->                        <div class="col-md-12">                            <label for="schedule_at" class="form-label">{{ __('Schedule At') }}</label>                            <input type="datetime-local" id="schedule_at" name="schedule_at" class="form-control">                        </div>                                                                </div>                </div>                <div class="modal-footer">                    <button type="submit" class="btn btn-primary">                        <span class="submit-btn">                             <x-icon name="send" height="20"/>                        </span>                        {{ __('Send Notification') }}</button>                </div>            </form>        </div>    </div></div>@push('scripts')    <script>        "use strict";        document.addEventListener('DOMContentLoaded', function () {            const notifyType = document.getElementById('modal_notify_type');            const titleRow = document.getElementById('modal_title_row');                        function toggleModalTitleField() {                if (notifyType.value === 'push') {                    titleRow.classList.add('d-none');                } else {                    titleRow.classList.remove('d-none');                }            }                        // Initial check            toggleModalTitleField();                        // On change event            notifyType.addEventListener('change', toggleModalTitleField);        });    </script>@endpush